import './logoutButton.css';

// Componente para el bot贸n de logout
function LogoutButton({ handleLogout, translations, language }) {
    return (
        <button className="logout-button" onClick={handleLogout}>
            {translations[language].logout}
        </button>
    );
}

// Componente para el bot贸n de enviar
function SendButton({ isFormValid, translations, language }) {
    return (
        <button type="submit" className="form-submit-button" disabled={!isFormValid}>
            {translations[language].send}
        </button>
    );
}

// Exportar ambos componentes desde este archivo
export { LogoutButton, SendButton };
