import React, { useState, useEffect } from 'react';
import ContactForm from './ContactForm';
import TranslatePage from './TranslatePage';

import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [weatherData, setWeatherData] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [formData, setFormData] = useState({
    nombre: '',
    fechaNacimiento: '',
    ciudad: '',
    email: '',
    telefono: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [message, setMessage] = useState('');
  const [language, SetLanguage]= useState('en')

  function handleLogin() {
    setIsLoggedIn(true);
  }

  function handleLogout() {
    setIsLoggedIn(false);
    setWeatherData([]);
    setSelectedCity('');
  }

  const handleCityClick = (city) => {
    setSelectedCity(city);
  };

  useEffect(() => {
    if (selectedCity) {
      fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${selectedCity}&appid=e4cab5d1b24f57fc3a3a01c13fdbc4de`)
        .then(response => response.json())
        .then(data => {
          setWeatherData(data.list);
        });
    }
  }, [selectedCity]);

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const updatedData = { ...prevData, [name]: value };
      const isValid = Object.values(updatedData).every((field) => field.trim() !== '');
      setIsFormValid(isValid);
      return updatedData;
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      setMessage('Form submitted successfully.');
    } else {
      setMessage('Please fill out all fields.');
    }
  };

  const languageChange=(e) =>{
    SetLanguage(e.target.value)
  }

  const translations={

    en:{
      london: "London",
      toronto: "Toronto",
      singapore: "Singapour",
      languages:"Languages",
      english:"English",
      spanish:"Spanish",
      login:"Login",
      logout:"Logout",
      send:"send",
      currentTemperature:"Current Temperature",
      maxTemperature: "Max Temperature",
      minTemperature: "Min Temperature",
      dayandTime:"Day and Time",
      name:"Name",
      brithday: "Brithday",
      city:"City",
      email:"Email",
      phonenumber:" Phone Number",
      contact:"Contact",
      password:"Password"

    },

    es:{
      london: "Londres",
      toronto: "Toronto",
      singapore: "Singapur",
      languages:"Idiomas",
      english:"Inglés",
      spanish:"Español",
      login:"Acceso",
      logout:"Cerrar sesión",
      send:"Enviar",
      currentTemperature:"Temperatura Actual",
      maxTemperature: "Temperatura Máxima",
      minTemperature: "Temperatura Mínima",
      dayandTime: "Día y hora",
      name:"Nombre",
      brithday:"Fecha de nacimiento",
      city:"Ciudad",
      email:"Correo electrónico",
      phonenumber:"Teléfono",
      contact:"Contacto",
      password: "Contraseña"

    }




  }

  return (
    <div className="App">
      <div className="head-container">
        <TranslatePage
        translations={translations}
        language={language}
        languageChange={languageChange}
        />
        {isLoggedIn && (
          <LogotButtom
          handleLogout={handleLogout}
          translations={translations}
          language={language}
          />
        )}
      </div>
      <div className="sidebar">
        {isLoggedIn ? (
          <>
            <div className="cities-container">
              <div className="london-city" onClick={() => handleCityClick('London')}>{translations[language].london}</div>
              <div className="toronto-city" onClick={() => handleCityClick('Toronto')}>{translations[language].toronto}</div>
              <div className="singapore-city" onClick={() => handleCityClick('Singapore')}>{translations[language].singapore}</div>

              <div className="main-content">
        {selectedCity && weatherData.length > 0 && (
          <div className="weather-data">
            {weatherData.slice(0, 3).map((temp) => (
              <div key={temp.dt} className="weather-info">
                <p>{translations[language].currentTemperature}: {temp.main.temp} K</p>
                <p>{translations[language].maxTemperature}: {temp.main.temp_max} K</p>
                <p>{translations[language].minTemperature}: {temp.main.temp_min} K</p>
                <p>{translations[language].dayandTime}: {temp.dt_txt}</p>
                <img
                  src={`http://openweathermap.org/img/wn/${temp.weather[0].icon}@2x.png`}
                  alt="Weather Icon"
                  className="weather-icon"
                />
              </div>
            ))}
          </div>
        )}
      </div>
            </div>

            <ContactForm
            translations={translations}
            language={language}
            formData={formData}
            handleFormChange={handleFormChange}
            handleFormSubmit={handleFormSubmit}
            message={message}
            isFormValid={!isFormValid}

            />
          </>
        ) : (
          <div className="login-container">
            <h2 className="login-title">{translations[language].login}</h2>
            <form className="login-form" onSubmit={(e) => {
              e.preventDefault();
              handleLogin();
            }}>
              <div className="form-group">
                <input type="text" className="email" placeholder={translations[language].email} />
              </div>
              <div className="form-group">
                <input type="password" className="password" placeholder={translations[language].password} />
              </div>
              <button className="button">{translations[language].login}</button>
            </form>
          </div>
        )}
      </div>

    </div>
  );
}

function LogotButtom(handleLogout,trans){
  <button className="logout-button" onClick={handleLogout}>{translations[language].logout}</button>
}





export default App;
