import React, { useState, useEffect } from 'react';
import './App.css';


function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [weatherData, setWeatherData] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [formData, setFormData] = useState({
    nombre: '',
    fechaNacimiento: '',
    ciudad: '',
    email: '',
    telefono: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [message, setMessage] = useState('');

  function handleLogin() {
    setIsLoggedIn(true);
  }

  function handleLogout() {
    setIsLoggedIn(false);
    setWeatherData([]); // Limpiar los datos del tiempo
    setSelectedCity(''); // Limpiar la ciudad seleccionada
  }

  const handleCityClick = (city) => {
    setSelectedCity(city);
  };

  useEffect(() => {
    if (selectedCity) {
      fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${selectedCity}&appid=e4cab5d1b24f57fc3a3a01c13fdbc4de`)
        .then(response => response.json())
        .then(data => {
          setWeatherData(data.list);
          console.log(data.list, "AQUI");
        });
      console.log(selectedCity,"SELECION DE CIUDAD")
    }
  }, [selectedCity]); // Ejecutar cuando selectedCity cambia

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const updatedData = { ...prevData, [name]: value };
      // Check if all fields are filled
      const isValid = Object.values(updatedData).every((field) => field.trim() !== '');
      setIsFormValid(isValid);
      return updatedData;
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      setMessage('Formulario enviado con Ã©xito');
    } else {
      setMessage('Por favor, rellena todos los campos.');
    }
  };

  return (
    <div className="App">
      <div className='head-container'>
        <label className='head'>
          Languages
          <select className='language-options'>
            <option value="english">English</option>
            <option value="spanish">Spanish</option>
          </select>
        </label>
        {isLoggedIn && (
          <button className='logout-button' onClick={handleLogout}>Logout</button>
        )}
      </div>
      {isLoggedIn ? (
        <div className="cities-container">
          <div className='london-city' onClick={() => handleCityClick('London')}>London</div>
          <div className='toronto-city' onClick={() => handleCityClick('Toronto')}>Toronto</div>
          <div className='singapore-city' onClick={() => handleCityClick('Singapore')}>Singapore</div>
        </div>
      ) : (
        <div className='login-container'>
          <h2>Login</h2>
          <form onSubmit={(e) => {
            e.preventDefault(); // Previene el comportamiento por defecto del formulario
            handleLogin(); // Llama a handleLogin para cambiar el estado
          }}>
            <div>
              <input type="text" className='email' placeholder='Email' />
            </div>
            <div>
              <input type="password" className='password' placeholder='Password' />
            </div>
            <button className='button'>Login</button>
          </form>
        </div>
      )}
      {selectedCity && weatherData.length > 0 && (
        <div className='weather-data'>
          {weatherData.slice(0, 3).map((temp) => (
            <div key={temp.dt} className="weather-info">
              <p>Current Temperature: {temp.main.temp} K</p>
              <p>Max Temperature: {temp.main.temp_max} K</p>
              <p>Min Temperature: {temp.main.temp_min} K</p>
              <p>Day and Time: {temp.dt_txt}</p>
              <img
                src={`http://openweathermap.org/img/wn/${temp.weather[0].icon}@2x.png`}
                alt="Weather Icon"
                className="weather-icon"
              />
            </div>
          ))}
        </div>
      )}
      {isLoggedIn && (
        <div className="contact-form-container">
          <h2>Contacto</h2>
          <form onSubmit={handleFormSubmit}>
            <div>
              <label>
                Nombre:
                <input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  onChange={handleFormChange}
                />
              </label>
            </div>
            <div>
              <label>
                Fecha de Nacimiento:
                <input
                  type="date"
                  name="fechaNacimiento"
                  value={formData.fechaNacimiento}
                  onChange={handleFormChange}
                />
              </label>
            </div>
            <div>
              <label>
                Ciudad:
                <input
                  type="text"
                  name="ciudad"
                  value={formData.ciudad}
                  onChange={handleFormChange}
                />
              </label>
            </div>
            <div>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleFormChange}
                />
              </label>
            </div>
            <div>
              <label>
                TelÃ©fono:
                <input
                  type="tel"
                  name="telefono"
                  value={formData.telefono}
                  onChange={handleFormChange}
                />
              </label>
            </div>
            <button type="submit" disabled={!isFormValid}>
              Enviar
            </button>
          </form>
          {message && <p>{message}</p>}
        </div>
      )}
    </div>
  );
}

export default App;
