import React, { useState, useEffect } from 'react';
import './App.css';


function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [weatherData, setWeatherData] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [formData, setFormData] = useState({
    nombre: '',
    fechaNacimiento: '',
    ciudad: '',
    email: '',
    telefono: '',
  });
  const [isFormValid, setIsFormValid] = useState(false);
  const [message, setMessage] = useState('');

  function handleLogin() {
    setIsLoggedIn(true);
  }

  function handleLogout() {
    setIsLoggedIn(false);
    setWeatherData([]); // Limpiar los datos del tiempo
    setSelectedCity(''); // Limpiar la ciudad seleccionada
  }

  const handleCityClick = (city) => {
    setSelectedCity(city);
  };

  useEffect(() => {
    if (selectedCity) {
      fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${selectedCity}&appid=e4cab5d1b24f57fc3a3a01c13fdbc4de`)
        .then(response => response.json())
        .then(data => {
          setWeatherData(data.list);
          console.log(data.list, "AQUI");
        });
      console.log(selectedCity,"SELECION DE CIUDAD")
    }
  }, [selectedCity]); // Ejecutar cuando selectedCity cambia

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const updatedData = { ...prevData, [name]: value };
      // Check if all fields are filled
      const isValid = Object.values(updatedData).every((field) => field.trim() !== '');
      setIsFormValid(isValid);
      return updatedData;
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      setMessage('Formulario enviado con Ã©xito');
    } else {
      setMessage('Por favor, rellena todos los campos.');
    }
  };

  return (<div className="App">
  <div className="sidebar">
    {isLoggedIn ? (
      <>
        <div className="sidebar-header">
          <label className="language-label">
            Languages
            <select className="language-select">
              <option value="english">English</option>
              <option value="spanish">Spanish</option>
            </select>
          </label>
          <button className="logout-button" onClick={handleLogout}>Logout</button>
        </div>
        <div className="cities-container">
          <div className="city-option" onClick={() => handleCityClick('London')}>London</div>
          <div className="city-option" onClick={() => handleCityClick('Toronto')}>Toronto</div>
          <div className="city-option" onClick={() => handleCityClick('Singapore')}>Singapore</div>
        </div>
        <div className="contact-form-container">
          <h2 className="contact-form-title">Contacto</h2>
          <form className="contact-form" onSubmit={handleFormSubmit}>
            <div className="form-group">
              <label className="form-label">Nombre:</label>
              <input
                type="text"
                name="nombre"
                className="form-input"
                value={formData.nombre}
                onChange={handleFormChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Fecha de Nacimiento:</label>
              <input
                type="date"
                name="fechaNacimiento"
                className="form-input"
                value={formData.fechaNacimiento}
                onChange={handleFormChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Ciudad:</label>
              <input
                type="text"
                name="ciudad"
                className="form-input"
                value={formData.ciudad}
                onChange={handleFormChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Email:</label>
              <input
                type="email"
                name="email"
                className="form-input"
                value={formData.email}
                onChange={handleFormChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">TelÃ©fono:</label>
              <input
                type="tel"
                name="telefono"
                className="form-input"
                value={formData.telefono}
                onChange={handleFormChange}
              />
            </div>
            <button type="submit" className="form-submit-button" disabled={!isFormValid}>
              Enviar
            </button>
          </form>
          {message && <p className="form-message">{message}</p>}
        </div>
      </>
    ) : (
      <div className="login-container">
        <h2 className="login-title">Login</h2>
        <form className="login-form" onSubmit={(e) => {
          e.preventDefault();
          handleLogin();
        }}>
          <div className="form-group">
            <input type="text" className="login-input" placeholder='Email' />
          </div>
          <div className="form-group">
            <input type="password" className="login-input" placeholder='Password' />
          </div>
          <button className="login-button">Login</button>
        </form>
      </div>
    )}
  </div>
  <div className="main-content">
    {selectedCity && weatherData.length > 0 && (
      <div className="weather-data">
        {weatherData.slice(0, 3).map((temp) => (
          <div key={temp.dt} className="weather-info">
            <p>Current Temperature: {temp.main.temp} K</p>
            <p>Max Temperature: {temp.main.temp_max} K</p>
            <p>Min Temperature: {temp.main.temp_min} K</p>
            <p>Day and Time: {temp.dt_txt}</p>
            <img
              src={`http://openweathermap.org/img/wn/${temp.weather[0].icon}@2x.png`}
              alt="Weather Icon"
              className="weather-icon"
            />
          </div>
        ))}
      </div>
    )}
  </div>
</div>
);
}

export default App;
