import React, {useState, useEffect} from 'react';
import './App.css';

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [weatherData, setWeatherData] = useState([]);
  const [selectedCity, setSelectedCity] = useState('London');

  function handleLogin() {
    setIsLoggedIn(true); 
  }
  function handleLogout() {
    setIsLoggedIn(false);
  }
  const handleCityClick = (city) => {
    setSelectedCity(city);
  }


  useEffect(() => {
    fetch('https://api.openweathermap.org/data/2.5/forecast?q=London&appid=e4cab5d1b24f57fc3a3a01c13fdbc4de')
      .then(response => response.json())
      .then(data => {
        setWeatherData(data.list);
        console.log(data.list,"AQUI")
      })

  }, [selectedCity]);





  return (

    <div className="App">

      <div className='weather-data'>
      {weatherData.slice(0, 4).map((temp) => (
       <div key={temp.dt}>

       <p>Current Temperature: {temp.main.temp} K</p>
       <p>Max Temperature: {temp.main.temp_max} K</p>
       <p>Min Temperature: {temp.main.temp_min} K</p>
       <p>dia y hora: {temp.dt_txt}</p>
       <img
            src={`http://openweathermap.org/img/wn/${temp.weather[0].icon}@2x.png`}
            alt="Weather Icon"
          />
     </div>
      ))}


      </div>
      <div className='head-container'>
      <label className='head'>
        Lenguages
        <select className='language-options'>
          <option value="english">English</option>
          <option value="spanish">Spanish</option>

        </select>
      </label>
      {isLoggedIn && (
          <button className='logout-button' onClick={handleLogout}>off</button>
        )}
      </div>

      {isLoggedIn ? (
        <div className="cities-container">
           <div className='london-city' onClick={() => handleCityClick('London')}>London</div>
          <div className='toronto-city' onClick={() => handleCityClick('Toronto')}>Toronto</div>
          <div className='singapur-city' onClick={() => handleCityClick('Singapore')}>Singapore</div>
        </div>
      ) : (

     <div className='login-container'>

       <h2>Inicio de sesi√≥n</h2>
       <form  onSubmit={(e) => {
              e.preventDefault(); // Previene el comportamiento por defecto del formulario
              handleLogin(); // Llama a handleLogin para cambiar el estado
            }}>
         <div>
         <input type="text" className='email'placeholder='Email'></input>
         </div>
         <div>
         <input type="password" className='password'placeholder='Password'></input>
         </div>
         <button className='button'>Click</button>
       </form>
     </div>
     )}
    </div>
  );
}

export default App;
